function Login({ usuario, iniciar }) {
    const handleSubmit = (event) => {
        event.preventDefault();
        const correo = event.currentTarget.emailInput.value;
        const contra = event.currentTarget.passwdInput.value;
        iniciarSesion('http://localhost:4000/login', { usuario: correo, contra: contra }).then((value) => {
            if (value.error != undefined) {
                if (value.username != '' || value.username != undefined) {
                    usuario(value.username)    
                    iniciar(true)
                }
                
            }
        })
        // usuario(correo)
        // iniciar(true)
    }
    async function iniciarSesion(url = '', data = {}) {
        const response = await fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });
    }
    return (
        <div>
                <form onSubmit={handleSubmit}>
                    <label>Correo Electronico</label>
                    <input type="email" id="emailInput" placeholder="Ingrese Email" required/>
                    <label>Contraseña</label>
                    <input type="password" id="passwdInput" placeholder="Ingrese Contraseña" required/>
                    <button type="submit">Ingresar</button>
                </form>
        </div>
    )
}
export default Login
