  const [perfilUsuario, setPerfilUsuario] = useState([]);
  const [registroPersonal, setRegistroPersonal] = useState('');
  const [miCuenta, setMiCuenta] = useState('');
  const [error, setError] = useState('');
  const { usuario } = useParams();

    useEffect(() => {
        fetchPerfil();
    const registroToken = localStorage.getItem('registroToken');
    if (registroToken == usuario) {
        setMiCuenta(true);
    }
    }, []);
    
    const fetchPerfil = () => {

  // Realiza la solicitud AJAX para obtener el perfil
  $.ajax({
    url: `http://localhost:4000/buscarPerfil/${usuario}`, // Usa la URL con el par치metro
    method: 'GET',
    dataType: 'json',
    success: (data) => {
      if (data.error) {
        console.error('Error al obtener el usuario:', data.error);
      } else {
        setPerfilUsuario(data);
        console.log(data);
      }
    },
    error: (error) => console.error('Error al obtener el usuario:', error),
  });
};
  

  return (
    <div>
       <h1>P치gina de perfil de {usuario}</h1>
    {perfilUsuario.map((perfilUsuario) => (
          <div key={perfilUsuario.registro_academico} className="card-publicacion">
          <p>Nombre: {perfilUsuario.nombre}</p>
          <p>Apellido: {perfilUsuario.apellidos}</p>
          <p>Correo Electr칩nico: {perfilUsuario.email}</p>
          {esMiPerfil && <button>Editar Perfil</button>}
            </div>
    ))}
    </div>
  );
}

export default Perfil;
