import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import $ from 'jquery';

function Perfil({ usuarioActual, esMiPerfil }) {
  const [perfilUsuario, setPerfilUsuario] = useState([]);
  const [registroPersonal, setRegistroPersonal] = useState('');
  const [error, setError] = useState('');
  const { usuario } = useParams();

    const fetchPublicaciones = () => {
    // Realiza la solicitud AJAX para obtener las publicaciones con filtros
    $.ajax({
      url: `http://localhost:4000/publicaciones?curso=${cursoSeleccionado}&catedratico=${catedraticoSeleccionado}`,
      method: 'GET',
      dataType: 'json',
      success: (data) => {
        setPublicaciones(data)
      console.log(data)},
      error: (error) => console.error('Error al obtener las publicaciones:', error),
    });
  };
  useEffect(() => {
    if (!esMiPerfil && registroPersonal) {
      // Realizar una solicitud al servidor para obtener el perfil del usuario
      fetch(`http://localhost:4000/buscarPerfil?usuario=${registroPersonal}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.error) {
            setError('Usuario no encontrado');
          } else {
            setPerfilUsuario(data);
          }
        })
        .catch((error) => console.error('Error al obtener el perfil del usuario:', error));
    } else if (esMiPerfil) {
      
      setPerfilUsuario(usuarioActual);
    }
  }, [usuarioActual, esMiPerfil, registroPersonal]);

  // Función para manejar cambios en el número de Registro Personal
  const handleRegistroPersonalChange = (e) => {
    setRegistroPersonal(e.target.value);
  };

  return (
    <div>
       <h1>Página de perfil de {usuario}</h1>

      {perfilUsuario && (
        <div>
          <p>Nombre: {perfilUsuario.nombre}</p>
          <p>Apellido: {perfilUsuario.apellido}</p>
          <p>Correo Electrónico: {perfilUsuario.correo}</p>
          {/* Otros campos del perfil */}
          {esMiPerfil && <button>Editar Perfil</button>}
        </div>
      )}
    </div>
  );
}

export default Perfil;
