function Login({ usuario, iniciar }) {
    const handleSubmit = (event) => {
        event.preventDefault();
        const registro_academico = event.currentTarget.numberInput.value;
        const contra = event.currentTarget.passwdInput.value;
        iniciarSesion('http://localhost:4000/login', { usuario: registro_academico, contra: contra }).then((value) => {    
            
            if (value.error != undefined) {
                console.log(value.username);
            }
            if (value.username != '' && value.username != undefined) {
                    usuario(value.username)    
                    iniciar(true)
                }
              
        })
        // usuario(correo)
        // iniciar(true)
    }
    async function iniciarSesion(url = '', data = {}) {
        const response = await fetch(url, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        });
        return response.json();
    }
    return (
        <div>
                <form onSubmit={handleSubmit}>
                    <label>Registro Academico</label>
                    <input type="number" id="numberInput" placeholder="Ingrese su Registro Academico" required/>
                    <label>Contraseña</label>
                    <input type="password" id="passwdInput" placeholder="Ingrese Contraseña" required/>
                    <button type="submit">Ingresar</button>
                </form>
        </div>
    )
}
export default Login
