import React, { useState, useEffect } from 'react';

function Home({ usuario }) {
  const [publicaciones, setPublicaciones] = useState([]);
  const [comentarioInput, setComentarioInput] = useState('');

  useEffect(() => {
    // Realiza una solicitud para obtener las publicaciones con comentarios
    fetch('http://localhost:4000/publicaciones')
      .then((response) => response.json())
      .then((data) => setPublicaciones(data))
      .catch((error) => console.error('Error al obtener las publicaciones:', error));
  }, []);

  const handleComentarioSubmit = (publicacionId) => {
    // Aquí debes enviar el comentario al servidor y actualizar las publicaciones
    // Puedes realizar una solicitud POST al servidor para agregar un comentario
    // y luego volver a obtener las publicaciones actualizadas.
    // Por razones de simplicidad, esta parte se omite en este ejemplo.
  };

  return (
    <div>
      <h2>Publicaciones</h2>
      {publicaciones.map((publicacion) => (
        <div key={publicacion.id_publicacion}>
          <h3>
            Nombre y Apellido: {publicacion.nombre_usuario} {publicacion.apellidos_usuario}
          </h3>
          {publicacion.curso && <p>Curso: {publicacion.curso}</p>}
          {publicacion.catedratico && <p>Catedrático: {publicacion.catedratico}</p>}
          <p>Mensaje: {publicacion.mensaje_publicacion}</p>
          <p>Fecha de Publicación: {publicacion.fecha_creacion}</p>

          {/* Input para publicar comentarios */}
          <input
            type="text"
            value={comentarioInput}
            onChange={(e) => setComentarioInput(e.target.value)}
            placeholder="Escribe un comentario..."
          />
          <button onClick={() => handleComentarioSubmit(publicacion.id_publicacion)}>Publicar Comentario</button>

          {/* Lista de comentarios */}
          <ul>
            {publicacion.comentarios.map((comentario) => (
              <li key={comentario.id_comentario}>{comentario.texto}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default Home;
