import React, { useState, useEffect } from 'react';

function Perfil({ usuarioActual, esMiPerfil }) {
  const [perfilUsuario, setPerfilUsuario] = useState(null);
  const [registroPersonal, setRegistroPersonal] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    if (!esMiPerfil && registroPersonal) {
      // Realizar una solicitud al servidor para obtener el perfil del usuario
      fetch(`http://localhost:4000/obtenerPerfil?registroPersonal=${registroPersonal}`)
        .then((response) => response.json())
        .then((data) => {
          if (data.error) {
            setError('Usuario no encontrado');
          } else {
            setPerfilUsuario(data);
          }
        })
        .catch((error) => console.error('Error al obtener el perfil del usuario:', error));
    } else if (esMiPerfil) {
      // Si es el perfil del usuario actual, puedes cargar los datos desde el estado o una solicitud al servidor.
      // Por ejemplo, si tienes los datos del usuario actual en el estado, puedes hacer:
      setPerfilUsuario(usuarioActual);
    }
  }, [usuarioActual, esMiPerfil, registroPersonal]);

  // Función para manejar cambios en el número de Registro Personal
  const handleRegistroPersonalChange = (e) => {
    setRegistroPersonal(e.target.value);
  };

  return (
    <div>
      {esMiPerfil ? (
        <h1>Mi Perfil</h1>
      ) : (
        <div>
          <h1>Perfil de Usuario</h1>
          <label>Número de Registro Personal:</label>
          <input
            type="text"
            value={registroPersonal}
            onChange={handleRegistroPersonalChange}
            placeholder="Ingrese el número de Registro Personal"
          />
          <button>Buscar</button>
          {error && <p>{error}</p>}
        </div>
      )}

      {perfilUsuario && (
        <div>
          <p>Nombre: {perfilUsuario.nombre}</p>
          <p>Apellido: {perfilUsuario.apellido}</p>
          <p>Correo Electrónico: {perfilUsuario.correo}</p>
          {/* Otros campos del perfil */}
          {esMiPerfil && <button>Editar Perfil</button>}
        </div>
      )}
    </div>
  );
}

export default Perfil;
